var Informer={getData:function(user,css){if(user===undefined){return}var informerDOM=jQuery("#fgr-informer");jQuery.ajax({dataType:'jsonp',data:user,url:'http://flashgameratings.ru/games/informer.js',success:function(data){if(data.count>=0){informerDOM.append(" ("+data.count+")")}if(data.count>0){informerDOM.addClass('fgr-new-games');if(css===undefined){css={'font-weight':'bold'}}informerDOM.css(css)}}})},forum:function(username,css){if(!username){return}this.getData({forum_user:username},css)},blog:function(username,css){if(!username){return}this.getData({blog_user:username},css)}};